import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    createStyles,
    Paper,
    Table,
    TableBody,
    TableCell,
    TableHead,
    TableRow,
    Theme,
    withStyles,
    WithStyles,
    Button,
} from '@material-ui/core';
import _ from 'lodash';
import { setLogout } from '../../actions';
import { browserHistory } from 'react-router';
import { useSelector, useDispatch } from 'react-redux';

const userstate = {
    datarecords: []
};

const UserTable = (state: any) => {

   /* const logstate = useSelector((state: any) => state.isLogged);
    console.log("======TOKEN=======");
    console.log(logstate.token);
    const users: any[] = [];
    console.log("======AUTHORIZATION=======");
    console.log('Bearer ' + logstate.token);
    axios.defaults.headers.common['Authorization'] = 'Bearer '+logstate.token;
    axios.get('https://localhost:44391/api/Dashboard')
        .then(response => {
            
            users.push(response.data);
            console.log(users);
            
            userstate.datarecords = users[0];
            console.log("========DATA=======");
            console.log(userstate.datarecords);
            
        })
        .catch(error => {

        })*/
    const [users, setUsers] = useState([]);
    const userdata: any[] = [];
    const logstate = useSelector((state: any) => state.isLogged);
    useEffect(() => {
        
        axios.defaults.headers.common['Authorization'] = 'Bearer ' + logstate.token;
        axios.get('https://localhost:44391/api/Dashboard')
            .then(response => {
                setUsers(response.data);

            })
            .catch(error => {

            })
    })

    const dispatch = useDispatch();
    const pageNavigation = () => {
        dispatch(setLogout());
        browserHistory.push("/login");
    };
    return (
        <div>
            <Button
                onClick={pageNavigation}>
                LOGOUT
            </Button>
        <Paper >
            
            <h2>Employee List</h2>

                <table>
                    <thead>

                        <tr>
                        <td><strong>Id</strong></td>
                        <td><strong>Name</strong></td>
                        <td><strong>Username</strong></td>
                        <td><strong>Email</strong></td>
                        </tr>
                    </thead>
                <tbody>
                        {users.map((u, index) => {
                            return <Tr key={index} user={u}/>
                        })}
                </tbody>
            </table>
        </Paper>
        </div>
    );
}
const Tr = ({ user }: { user: any }) => {
    return (
        <tr>
            <td>{user.user_id}</td>
            <td>{user.fullname}</td>
            <td>{user.username}</td>
            <td>{user.useremail}</td>
        </tr>)
}
export default UserTable;
