import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    createStyles,
    Paper,
    Table,
    TableBody,
    TableCell,
    TableHead,
    TableRow,
    Theme,
    withStyles,
    WithStyles,
} from '@material-ui/core';
import _ from 'lodash';
//import classes from '*.module.css';

export interface IState {
    apiurl: string;
    datarecords: any[];
    datacolumns: any[];
}

export default class FetchTableData extends React.Component{

    //constructor(props:IState) {
    //    super(props);
    //    this.state = {
    //        apiurl:'',
    //        datarecords: [],
    //        datacolumns: [],
    //    }
    //}

    state = {
        datarecords: [],
        datacolumns: [],
    }

    componentDidMount() {
        axios.get('http://dummy.restapiexample.com/api/v1/employees')
            .then(response => {

                this.setState({ datarecords: response.data });
                console.log(this.state.datarecords);
                this.extractColumnNames();
                
            })
            .catch(error => {
               
            })
    }
    private extractColumnNames() {
        const firstrecord = _.keys(this.state.datarecords[0]);
        this.setState({ datacolumns: firstrecord });
        console.log(firstrecord);
    }
   
    render() {
        const datarecords = this.state.datarecords;
        return(
        <Paper >
            <p>Employee List</p>

            <Table >
                    <TableHead>

                    <TableRow>
                        <TableCell>Id</TableCell>
                        <TableCell>Employee Name</TableCell>
                        <TableCell>Salary</TableCell>
                        <TableCell>Age</TableCell>
                    </TableRow>
                </TableHead>
                    <TableBody>
                        {datarecords.map(index => {
                            return (
                                <p key={index}>Hello}!</p>
                            );
                        })}
                        
                    </TableBody>   
            </Table>
        </Paper>
            
        );
    }
}
//export default FetchTableData;
