import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    createStyles,
    Paper,
    Table,
    TableBody,
    TableCell,
    TableHead,
    TableRow,
    Theme,
    withStyles,
    WithStyles,
} from '@material-ui/core';
import _ from 'lodash';
import employees from '../../data/employees.json';
import { setDashboardSuccess, setDashboardError } from '../../actions';
import { useSelector, useDispatch } from 'react-redux';
import { browserHistory } from 'react-router';

export interface IState {
    apiurl: string;
    datarecords: any[];
    datacolumns: any[];
}

export default class FetchTableData extends React.Component{

    
    state = {
        datarecords: [],
    }
    //const dispatch = useDispatch();

    componentDidMount() {
        //const dashboardtate = useSelector((state: any) => state.dashboardData);
        //const dispatch = useDispatch();
       
        
        axios.get('https://localhost:44391/api/Dashboard')
            .then(response => {

                this.setState({ datarecords: response.data });
                console.log(this.state.datarecords);
                //dispatch(setDashboardSuccess());
                //console.log("status " + dashboardtate);
            })
            .catch(error => {
                //dispatch(setDashboardError());
                //console.log("status " + dashboardtate);
            })
    }
     buttonClicked=()=>{
         browserHistory.push("/login");
     }
    render() {
        //const token = localStorage.getItem("token");
        //if (token == null) {
        //    return browserHistory.push("/login");
        //}
        const datarecords = this.state.datarecords;
        
        return (
           
            <Paper >
                <button onClick={this.buttonClicked}>logout</button>
                <h2>Employee List</h2>
               
            <Table >
                    <TableHead>

                    <TableRow>
                            <TableCell><strong>Id</strong></TableCell>
                            <TableCell><strong>Name</strong></TableCell>
                            <TableCell><strong>Username</strong></TableCell>
                            <TableCell><strong>Email</strong></TableCell>
                    </TableRow>
                </TableHead>
                    <TableBody>
                        {datarecords.map(row => {
                            return (

                                <TableRow key={row["user_id"]}>
                                    <TableCell>{row["user_id"]}</TableCell>
                                    <TableCell>{row["fullname"]}</TableCell>
                                    <TableCell>{row["username"]}</TableCell>
                                    <TableCell>{row["useremail"]}</TableCell>
                                    
                                </TableRow>
                            );
                        })}
                    </TableBody>   
            </Table>
        </Paper>
            
        );
    }
}
//export default FetchTableData;
