import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    createStyles,
    Paper,
    Table,
    TableBody,
    TableCell,
    TableHead,
    TableRow,
    Theme,
    withStyles,
    WithStyles,
} from '@material-ui/core';
import _ from 'lodash';
import { useSelector } from 'react-redux';

const userstate = {
    datarecords: []
};

const UserTable = (state: any) => {

    const logstate = useSelector((state: any) => state.isLogged);
    console.log("======TOKEN=======");
    console.log(logstate.token);
    const users: any[] = [];
    axios.defaults.headers.common['Authorization'] = 'Bearer '+logstate.token;
    axios.get('https://localhost:44391/api/Dashboard')
        .then(response => {
            
            users.push(response.data);
            console.log(users);
            //console.log(users[0]);
            //const list: any[] = users[0];
            userstate.datarecords = users[0];
            console.log("========");
            console.log(userstate.datarecords);
            
        })
        .catch(error => {

        })
    return (
      
        <Paper >
            <h2>Employee List</h2>
            
            <Table >
                <TableHead>

                    <TableRow>
                        <TableCell><strong>Id</strong></TableCell>
                        <TableCell><strong>Name</strong></TableCell>
                        <TableCell><strong>Username</strong></TableCell>
                        <TableCell><strong>Email</strong></TableCell>
                    </TableRow>
                    
                    
                </TableHead>
                <TableBody>

                    {userstate.datarecords.map(row => {
                        return (

                            <TableRow key={row["user_id"]}>
                                <TableCell>{row["user_id"]}</TableCell>
                                <TableCell>{row["fullname"]}</TableCell>
                                <TableCell>{row["username"]}</TableCell>
                                <TableCell>{row["useremail"]}</TableCell>

                            </TableRow>
                        );
                    })}
                </TableBody>  
            </Table>
        </Paper>

    );
}

export default UserTable;
